.notification-wrapper {
  position: fixed;
  left: 0;
  top: 0;
  bottom: 0;
  right: 0;
  z-index: 9999999;
  font-family: Helvetica, Arial, sans-serif;
}
.notification-wrapper .backdrop {
  position: fixed;
  width: 100%;
  height: 100%;
  left: 0;
  top: 0;
  background-color: rgba(0, 0, 0, 0.5);
}
.notification-wrapper .notification {
  display: block;
  position: relative;
  width: 100%;
  height: 70px;
  color: #fff;
  z-index: 1;
  text-align: center;
  font-size: 16px;
  line-height: 70px;
}
.notification-wrapper .notification .message, .notification-wrapper .notification .buttons {
  display: inline;
}
.notification-wrapper .notification.success {
  background-color: #38d189;
}
.notification-wrapper .notification.info {
  background-color: #3899d1;
}
.notification-wrapper .notification.warning {
  background-color: #ff9f1c;
}
.notification-wrapper .notification.danger {
  background-color: #f9705e;
}
.notification-wrapper .notification.on {
  animation: slideIn 0.2s ease-in;
}
.notification-wrapper .notification.off {
  animation: slideOut 0.3s ease-in;
}
.notification-wrapper .button {
  display: inline-block;
  text-transform: uppercase;
  border-style: none;
  border-radius: 3px;
  background: transparent;
  min-height: 32px;
  min-width: 32px;
  text-align: center;
  margin-left: 15px;
  line-height: normal;
}
.notification-wrapper .buttons .button {
  border: 2px solid #fff;
  color: #ffffff;
}
.notification-wrapper .close-btn {
  position: absolute;
  right: 15px;
  top: 18px;
}
.notification-wrapper .close-btn svg {
  fill: #ffffff;
  width: 16px;
  height: 16px;
}
.notification-wrapper.on {
  animation: fadeIn 0.3s ease-in;
}
.notification-wrapper.off {
  animation: fadeOut 0.3s ease-in;
}
@keyframes fadeIn {
  0% {
    opacity: 0;
  }
  100% {
    opacity: 100;
  }
}
@keyframes fadeOut {
  0% {
    opacity: 100;
  }
  100% {
    opacity: 0;
  }
}
@keyframes slideIn {
  0% {
    margin-top: -70px;
  }
  100% {
    margin-top: 0;
  }
}
@keyframes slideOut {
  0% {
    margin-top: 0;
  }
  100% {
    margin-top: -70px;
  }
}
